import React, { useState, useEffect } from "react";
import './card.css';

const Home = ()=>{
    const [data, setData] = useState('');
    const [city, setCity] = useState('');
    const [cityLoc, setCityLoc] = useState('');
    const [otherData, setOtherData] = useState('');
  
    const [lat, setLat] = useState('');
    const [lon, setLon] = useState('');

    useEffect(() => {
        const fetchApi = async () => {
          const url = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=668cdacb8d0408442dda055fa313eded`
          const response = await fetch(url);
          const resJson = await response.json();
          setCityLoc(resJson.name);
          setData(resJson.name);
          navigator.geolocation.getCurrentPosition((position)=>{
            setLat(position.coords.latitude);
            setLon(position.coords.longitude);
            });
        }
      
          fetchApi();
        }, []);
        useEffect(() => {
            const fetchApi = async () => {
              const url = `https://api.openweathermap.org/data/2.5/weather?q=${data}&units=metric&APPID=668cdacb8d0408442dda055fa313eded`;
              const response = await fetch(url);
              const resJson = await response.json();
              setCity(resJson.main);
              setOtherData(resJson.weather);
            };
        
            fetchApi();
          }, [data]);
}

export default Home;
