import React, { useState, useEffect } from "react";
import "./card.css";
import axios from 'axios';

const Home = () => {
  const [weather, setWeather] = useState("");
  const [cond, setCond] = useState("");
  const [city, setCity] = useState("");
  const [lat, setLat] = useState('');
  const [lon, setLon] = useState('');

  navigator.geolocation.getCurrentPosition((position)=>{
    setLat(position.coords.latitude);
    setLon(position.coords.longitude);
    });

  const fetchApi = async (e) => {

    e.preventDefault();
    const loc = e.target.elements.loc.value;
    const url = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=668cdacb8d0408442dda055fa313eded`;
    const req = axios.get(url);
    const res = await req;
    setWeather(res.main);
    setCity(res.name);
    setCond(res.weather)
  };
  fetchApi();
  return (
    <>
      <div class="wrapper">
        <div class="card">
          {!city ? (
            <div className="info">
              <h3>No Data Found</h3>
            </div>
          ) : (
            <>
              <div className="info">
                <img
                  className="image"
                  src={`http://openweathermap.org/img/wn/${otherData[0].icon}@2x.png`}
                />
                <h1 className="card-title1">{city}</h1>
                <h3 className="card-title">{weather.temp} °C</h3>
                <h3 className="card-title">{cond[0].main}</h3>
                <p className="card-content">
                  Min : {city.temp_min} °C | Max : {city.temp_max} °C
                </p>
              </div>
            </>
          )}
        </div>
      </div>
    </>
  );
};

export default Home;
