import React, { useState, useEffect } from "react";
import './forecast.css';
import leftImg from './images/bgleft.png'
const apiKey = "ce7b63cece294cbb99183108231912";

const Searchpage = () => {

    const [city, setCity] = useState('');
    const [cityName, setCityName] = useState('');
    const [cityRegion, setCityRegion] = useState('');
    const [ID, setID] = useState('');

    const URL = `http://api.weatherapi.com/v1/current.json?key=${apiKey}&q=${city}&aqi=yes`;

    let IDURL = `http://api.weatherapi.com/v1/search.json?key=${apiKey}&q=${city}`
    useEffect(() => {
        const GetID = async () => {
            const response = await fetch(IDURL);
            const Rjson = await response.json();
            setID(Rjson[0].id);
            setCityName(Rjson[0].name);
            setCityRegion(Rjson[0].region);
            console.log(Rjson);
        }
        GetID();

    }, [city]);

    const GetInfo = ()=>{

    }

    return (
        <>
            <div className="container_forecast">
                <div className="container_center">
                    <div className="left_forecast">

                        <div className="search_div">
                            <i className='fa-solid fa-search search_glass'></i>
                            <input type="text" placeholder="Search Location"
                                className='search_bar'
                                onChange={(e) => {
                                    setCity(e.target.value);
                                }}
                                onKeyDown={(event) => {
                                    if (event.key === "Enter") {
                                        GetInfo();
                                    }
                                }}
                            />
                            
                        </div>
                        <div className="datetime">
                            <div>
                                <div className="citytime"><i class="fa-solid fa-location-dot arrow"></i> {cityName},{cityRegion} </div>
                                <div className="date"></div>
                            </div>
                            <div>
                                <div className='citytime'> </div>
                                <div className="date"></div>
                            </div>
                        </div>
                        <div className="temp_div">
                            <p className="temperature">30 &deg;C</p>
                            <div className="day_weather">Sunny <img className="weather_icon" alt="Error" src="" /></div>
                        </div>
                    </div>
                </div>
            </div>

        </>
    )
}

export default Searchpage;
