import React, { useState, useEffect } from "react";
import './card.css';

const Home = ()=>{
    const [data, setData] = useState('');
    const [city, setCity] = useState('');
    const [cityLoc, setCityLoc] = useState('');
    const [otherData, setOtherData] = useState('');
  
    const [lat, setLat] = useState('');
    const [lon, setLon] = useState('');

    useEffect(() => {
        const fetchApi = async () => {
          const url = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=668cdacb8d0408442dda055fa313eded`
          const response = await fetch(url);
          const resJson = await response.json();
          setCityLoc(resJson.name);
          setData(resJson.name);
          navigator.geolocation.getCurrentPosition((position)=>{
            setLat(position.coords.latitude);
            setLon(position.coords.longitude);
            });
        }
      
          fetchApi();
        }, []);
        useEffect(() => {
            const fetchApi = async () => {
              const url = `https://api.openweathermap.org/data/2.5/weather?q=${data}&units=metric&APPID=668cdacb8d0408442dda055fa313eded`;
              const response = await fetch(url);
              const resJson = await response.json();
              setCity(resJson.main);
              setOtherData(resJson.weather);
            };
        
            fetchApi();
          }, [data]);

          return (
            <>
              <h1 className="head">Weather App</h1>
              <div class="wrapper">
                <div class="card">
                  <div className="inputData">
                    <label id="label">Search</label>
                    <input
                      type="search"
                      className="inputField"
                      placeholder="Search City"
                      value={data}
                      onChange={(event) => {
                        setData(event.target.value);
                      }}
                    />
                  </div>
                  {!city ? (
                    <div className="info">
                      <h3>No Data Found</h3>
                    </div>
                  ) : (
                    <>
                      <div className="info">
                        <img className="image" src={`http://openweathermap.org/img/wn/${otherData[0].icon}@2x.png`} />
                        <h1 className="card-title1">{data}</h1>
                        <h3 className="card-title">{city.temp} °C</h3>
                        <h3 className="card-title">{otherData[0].main }</h3>
                        <p className="card-content">
                          Min : {city.temp_min} °C | Max : {city.temp_max} °C
                        </p>
                      </div>
                    </>
                  )}
                </div>
              </div>
            </>
          );
}

export default Home;
