import React, { useState } from 'react';
import './forecast.css';
import leftImg from './images/bgleft.png'
const apiKey = "edf4ce5a3b2ff94ead4cce61d6c9e5b5";

let cityName = "";
const URL = `https://api.openweathermap.org/data/2.5/forecast?q=${cityName}&appid=${apiKey}&units=metric`;
const GetInfo = () =>{
    const fetchedData = async () => {
        const url = `https://api.openweathermap.org/data/2.5/weather?q=${data}&units=metric&APPID=${API}`;
        const response = await fetch(url);
        const resJson = await response.json();
        setCity(resJson.main);
        setOtherData(resJson.weather);
      };
}


const Forecast = () => {
    const [city,setCity] = useState('');
    cityName = city;

    return (
        <>
            <div className="container_forecast">
                <div className="container_center">
                    <div className="left_forecast">

                        <div className="search_div">
                            <i className='fa-solid fa-search search_glass'></i>
                            <input type="text" placeholder="Search Location" 
                                className='search_bar' 
                                onChange={(e)=>{setCity(e.target.value)}} 
                                onKeyDown={(event) => {
                                    if (event.key === "Enter") {
                                        GetInfo();
                                    }
                                }}/>
                        </div>
                        <div className="datetime">
                            <div>
                                <div className="citytime"><i class="fa-solid fa-location-dot arrow"></i> Kalyan </div>
                                <div className="date">18 Dec 2023</div>
                            </div>
                            <div>
                                <div className='citytime'>10:20</div>
                                <div className='date'>AM</div>
                            </div>
                        </div>
                        <div className="temp_div">
                            <p className="temperature">30 &deg; C</p>
                            <p className="day_weather"><i class="fa-solid fa-sun"></i> Sunny Day</p>
                        </div>
                        {/* <div className="left_bottom"></div> */}
                    </div>
                    <div className="right_forecast"></div>
                </div>
            </div>
        </>)
}

export default Forecast;
