import React, { useState, useEffect } from "react";
import "./card.css";
import axios from "axios";

const Home = () => {
  const [data, setData] = useState("");
  const [weather, setWeather] = useState("");
  const [city, setCity] = useState("");
  const [lat, setLat] = useState("");
  const [lon, setLon] = useState("");
  const url = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=668cdacb8d0408442dda055fa313eded`;

  const fetchApi = async () => {
    try {
      const res = await axios.get(url);
      setData(res.data);
      setCity(data.name);
      setWeather(data.weather[0].main);

    } catch (error) {
        console.log(error);
    }
  };
  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      setLat(position.coords.latitude);
      setLon(position.coords.longitude);
    });

    fetchApi();
  }, []);

  return (
    <>
      <div className="wrapper">
        <div className="card">
          {!city ? (
            <div className="info">
              <h3>No Data Found</h3>
            </div>
          ) : (
            <>
              <div className="info">
                <img
                  className="image"
                  src={`http://openweathermap.org/img/wn/${weather.icon}@2x.png`}
                />
                <h1 className="card-title1">{city}</h1>
                <h3 className="card-title">{data.main.temp} °C</h3>
                <h3 className="card-title">{weather}</h3>
                <p className="card-content">
                  Min : {weather.temp_min} °C | Max : {weather.temp_max} °C
                </p>
              </div>
            </>
          )}
        </div>
      </div>
    </>
  );
};

export default Home;
