import React, { useState, useEffect } from "react";
import "./card.css";

const Home = () => {
  const url = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=668cdacb8d0408442dda055fa313eded`;
  const [weather, setWeather] = useState('');
  const [city, setCity] = useState('');
  
  const apiCall = async (e) => {
    e.preventDefault()
    const loc = e.target.elements.loc.value
    const url = `https://api.openweathermap.org/data/2.5/weather?q=${loc}&appid=${apiKey}`;
    const req = axios.get(url);
    const res = await req;
    setWeather({
        descp: res.data.weather[0].description,
        temp: res.data.main.temp,
        city: res.data.name,
        humidity: res.data.main.humidity,
        press: res.data.main.pressure,
    })

    setCity(res.data.name)

}
  return (
    <>
      <div class="wrapper">
        <div class="card">
          {!city ? (
            <div className="info">
              <h3>No Data Found</h3>
            </div>
          ) : (
            <>
              <div className="info">
                <img
                  className="image"
                  src={`http://openweathermap.org/img/wn/${otherData[0].icon}@2x.png`}
                />
                <h1 className="card-title1">{cityLoc.name}</h1>
                <h3 className="card-title">{city.temp} °C</h3>
                <h3 className="card-title">{otherData[0].main}</h3>
                <p className="card-content">
                  Min : {city.temp_min} °C | Max : {city.temp_max} °C
                </p>
              </div>
            </>
          )}
        </div>
      </div>
    </>
  );
};

export default Home;
