import React, { useEffect, useState } from 'react';
import './forecast.css';
import leftImg from './images/bgleft.png'
const apiKey = "edf4ce5a3b2ff94ead4cce61d6c9e5b5";


const Forecast = () => {

    const date = new Date();

    const Days = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];

    let weekDay = Days[date.getDay() - 1];
    let day = date.getDate();
    let year = date.getFullYear();
    // let weekDay = date.toString().slice(0,3);
    let month = date.toString().slice(4, 8);
    let time = date.toString().slice(16, 21);


    let FullDate = `${day} ${month} ${year}`;

    const [city, setCity] = useState('kalyan');
    const [data, setData] = useState([]);
    const [imageURL, setImageURL] = useState('');
    const [filteredData, setReqData] = useState([]);

    const [todayDate,setTodayDate] = useState('');
    const [temp, setTemp] = useState('');
    const [clouds, setClouds] = useState('');

    const URL = `https://api.openweathermap.org/data/2.5/forecast?q=${city}&appid=${apiKey}&units=metric`;
    const hourly_data = [];

    useEffect(() => {
        const fetchedData = async () => {
            const response = await fetch(URL);
            const resJson = await response.json();
            setData(resJson.list);
            setTemp(data[0].main.temp);
            setClouds(data[0].weather[0].main);
            setImageURL(`http://openweathermap.org/img/wn/${data[0].weather[0].icon}@2x.png`);

            let curr_day_time = Number(time.slice(0,2));
            console.log(curr_day_time);
            for (let i = 0; i < 5; i++) {
                let curr_temp = data[i].main.temp;
                let img_icon_url = data[i].weather[0].icon;
                let daily_data_object = {
                    "temp": curr_temp,
                    "url": img_icon_url,
                    "time":`${curr_day_time}:00`
                }
                curr_day_time = curr_day_time + 3;
                hourly_data.push(daily_data_object);
            }
            setReqData(hourly_data);
        };
        fetchedData();
    }, [city]);


    return (
        <>
            <div className="container_forecast">
                <div className="container_center">
                    <div className="left_forecast">

                        <div className="search_div">
                            <i className='fa-solid fa-search search_glass'></i>
                            <input type="text" placeholder="Search Location"
                                className='search_bar'
                                onKeyDown={(event) => {
                                    if (event.key === "Enter") {
                                        setCity(event.target.value)
                                    }
                                }}
                            />
                        </div>
                        <div className="datetime">
                            <div>
                                <div className="citytime"><i class="fa-solid fa-location-dot arrow"></i> {city} </div>
                                <div className="date"> {FullDate} </div>
                            </div>
                            <div>
                                <div className='citytime'> {time} </div>
                                <div className="date"> {weekDay} </div>
                            </div>
                        </div>
                        <div className="temp_div">
                            <p className="temperature">{temp} &deg;C</p>
                            <div className="day_weather">{clouds} <img className="weather_icon" alt="Error" src={imageURL} /></div>
                        </div>
                        {/* <div className="left_bottom"></div> */}
                    </div>
                    <div className="right_forecast">
                        <div className="dailyForecast">
                            <p className="daily_title"><i class="fa-regular fa-clock"></i> Upcoming Hours</p>
                            <div className="dailyForecast_div">
                                {filteredData.slice(0, 1).map((item) => {
                                    return (
                                        <div className="daily_div">
                                            <p className="daily_day">Now</p>
                                            <p className="daily_temp">{item.temp} &deg;c</p>
                                            <img className="weather_icon_daily" alt="Error" src={`http://openweathermap.org/img/wn/${item.url}@2x.png`} />
                                        </div>
                                    )
                                })
                                }
                                {filteredData.slice(1,).map((item) => {
                                    return (
                                        <div className="daily_div1">
                                            <p className="daily_day">{item.time}</p>
                                            <p className="daily_temp">{item.temp} &deg;c</p>
                                            <img className="weather_icon_daily" alt="Error" src={`http://openweathermap.org/img/wn/${item.url}@2x.png`} />
                                        </div>
                                    )
                                })
                                }
                            </div>
                        </div>

                        <div className="dailyForecast">
                            <p className="daily_title"><i class="fa-regular fa-clock"></i> Next Days</p>
                            <div className="dailyForecast_div">
                                {filteredData.slice(0, 1).map((item) => {
                                    return (
                                        <div className="daily_div">
                                            <p className="daily_day">Today</p>
                                            <p className="daily_day">{curr_date}</p>
                                            <p className="daily_temp">{item.temp} &deg;c</p>
                                            <img className="weather_icon_daily" alt="Error" src={`http://openweathermap.org/img/wn/${item.url}@2x.png`} />
                                        </div>
                                    )
                                })
                                }
                                {filteredData.slice(1,).map((item) => {
                                    return (
                                        <div className="daily_div1">
                                            <p className="daily_day">{item.time}</p>
                                            <p className="daily_temp">{item.temp} &deg;c</p>
                                            <img className="weather_icon_daily" alt="Error" src={`http://openweathermap.org/img/wn/${item.url}@2x.png`} />
                                        </div>
                                    )
                                })
                                }
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>)
}

export default Forecast;
